mapper.select:
select 

from moirai_oauth2_client
where 1=1

    and client_name = #{clientName,jdbcType=VARCHAR}


    and client_id = #{clientId,jdbcType=VARCHAR}


    and client_secret = #{clientSecret,jdbcType=VARCHAR}


    and token_key = #{tokenKey,jdbcType=VARCHAR}


    and SYSTEM_CODE = #{systemCode,jdbcType=VARCHAR}

select 

from moirai_oauth2_client
where 1=1

   and client_name = #{clientName,jdbcType=VARCHAR}


    and client_id = #{clientId,jdbcType=VARCHAR}


    and client_secret = #{clientSecret,jdbcType=VARCHAR}


    and token_key = #{tokenKey,jdbcType=VARCHAR}


    and SYSTEM_CODE = #{systemCode,jdbcType=VARCHAR}


    and RSA_PUBLIC = #{rsaPublic,jdbcType=VARCHAR}


    and RSA_PRIVATE = #{rsaPrivate,jdbcType=VARCHAR}


    and SECRET_DOMAIN = #{secretDomain,jdbcType=VARCHAR}


