mapper.select:
select

from moirai_product_resource
where 1=1

    AND PRODUCT_ID = #{productId,jdbcType=BIGINT}


    AND RESOURCE_ID = #{resourceId,jdbcType=BIGINT}


    AND CREATETIME = #{createtime,jdbcType=BIGINT}


    AND CREATER = #{creater,jdbcType=VARCHAR}


    AND PRODUCT_RESOURCE_ID = #{productResourceId,jdbcType=VARCHAR}

ORDER BY CREATETIME DESC

mapper.delete:
delete from moirai_product_resource
where 1=1

    AND PRODUCT_ID = #{productId,jdbcType=BIGINT}


    AND RESOURCE_ID = #{resourceId,jdbcType=BIGINT}


    AND PRODUCT_RESOURCE_ID = #{productResourceId,jdbcType=VARCHAR}


    AND 1 = 2

mapper.update:
update moirai_product_resource
set PRODUCT_ID = #{productId,jdbcType=BIGINT},
  RESOURCE_ID = #{resourceId,jdbcType=BIGINT},
  CREATETIME = #{createtime,jdbcType=BIGINT},
  CREATER = #{creater,jdbcType=VARCHAR}
where PRODUCT_RESOURCE_ID = #{productResourceId,jdbcType=BIGINT}

mapper.delete:
delete from moirai_product_resource
where PRODUCT_ID = #{productId,jdbcType=BIGINT}

    and PRODUCT_RESOURCE_ID not in

        #{item}
