SELECT , RES.TENANT_ID, RES.SOURCE_FLAG FROM moirai_resource_tenant RES ,moirai_product_resource pr WHERE pr.RESOURCE_ID = RES.RESOURCE_ID AND RES.DEL_FLAG = 'N' AND pr.PRODUCT_ID = #{productId,jdbcType=BIGINT} AND RES.TENANT_ID = #{tenantId,jdbcType=BIGINT}
AND RES.RESOURCE_ID = #{resourceId,jdbcType=BIGINT}
AND RES.PID = 0
UNION SELECT , RES.TENANT_ID, RES.SOURCE_FLAG FROM moirai_resource_tenant RES ,moirai_product_resource pr WHERE pr.RESOURCE_ID = RES.RESOURCE_ID AND RES.DEL_FLAG = 'N' AND pr.PRODUCT_ID = #{productId,jdbcType=BIGINT} AND RES.TENANT_ID = #{tenantId,jdbcType=BIGINT}
AND RES.PID = #{resourceId,jdbcType=BIGINT}
AND RES.PID = 0
ORDER BY RESOURCE_TYPE, ORDER_INDEX
