mapper.select:
SELECT DISTINCT re.RESOURCE_ID
FROM moirai_user_authz authz, moirai_role ro, moirai_role_resource re
WHERE
authz.role_id = ro.ROLE_ID
AND ro.ROLE_ID = re.ROLE_ID
AND ro.DEL_FLAG = 'N'
AND ro.USE_FLAG = 'Y'

    AND authz.user_id = #{userId, jdbcType=BIGINT}


    AND authz.tenant_id = #{tenantId, jdbcType=BIGINT}


    AND authz.auth_org = #{authOrg, jdbcType=BIGINT}


    AND authz.role_id = #{roleId, jdbcType=BIGINT}

SELECT re.ROLE_RESOURCE_ID, re.ROLE_ID, re.RESOURCE_ID, re.ORG_ID, re.TENANT_ID, re.CREATER, re.CREATETIME,
re.MODIFY_USER, re.MODIFY_TIME, re.PRODUCT_ID, re.INCLUDE_SON
FROM moirai_role_resource re left join moirai_user_authz authz on re.ROLE_ID = authz.role_id
WHERE 1=1

    AND authz.user_id = #{userId, jdbcType=BIGINT}


    AND authz.tenant_id = #{tenantId, jdbcType=BIGINT}


    AND authz.auth_org = #{authOrg, jdbcType=BIGINT}


    AND authz.role_id = #{roleId, jdbcType=BIGINT}

select

from moirai_role_resource
where 1=1

    AND ROLE_ID = #{roleId,jdbcType=BIGINT}


    AND RESOURCE_ID = #{resourceId,jdbcType=BIGINT}


    AND ORG_ID = #{orgId,jdbcType=BIGINT}


    AND TENANT_ID = #{tenantId,jdbcType=BIGINT}


    AND CREATER = #{creater,jdbcType=VARCHAR}


    AND CREATETIME = #{createtime,jdbcType=BIGINT}


    AND MODIFY_USER = #{modifyUser,jdbcType=VARCHAR}


    AND MODIFY_TIME = #{modifyTime,jdbcType=BIGINT}


    AND PRODUCT_ID = #{productId,jdbcType=BIGINT}

ORDER BY CREATETIME DESC

mapper.delete:
delete from moirai_role_resource


        and PRODUCT_ID = #{productId,jdbcType=BIGINT}


        and ROLE_ID = #{roleId,jdbcType=BIGINT}


        and RESOURCE_ID = #{resourceId,jdbcType=BIGINT}


        and 1 = 2
