mapper.select:
select

from moirai_user_login_history


        and ulh_id = #{ulhId, jdbcType=BIGINT}


        and tenant_id = #{tenantId, jdbcType=BIGINT}


        and user_id = #{userId, jdbcType=BIGINT}


        and org_id = #{orgId, jdbcType=BIGINT}


        and role_id = #{roleId, jdbcType=BIGINT}


        and login_time = #{loginTime, jdbcType=BIGINT}


        and creater = #{creater, jdbcType=VARCHAR}


        and create_time = #{createTime, jdbcType=BIGINT}


        and login_type = #{loginType, jdbcType=VARCHAR}


        and client_id = #{clientId, jdbcType=VARCHAR}


order by login_time

select oc.client_id clientId, oc.client_name clientName, count(1) userCount
from moirai_user_login_history lh left join moirai_oauth2_client oc on lh.client_id = oc.client_id


        and login_time < #{loginEndTime, jdbcType=BIGINT}


        and login_time > #{loginBeginTime, jdbcType=BIGINT}


group by lh.client_id, oc.client_name

select lh.ulh_id ulhId, lh.login_type loginType, lh.login_time loginTime, lh.user_id userId, lh.tenant_id tenantId
from moirai_user_login_history lh

    left join moirai_user u on u.user_id = lh.user_id


    left join moirai_org o on lh.tenant_id = o.tenant_id and o.parent_org = 0



        and lh.client_id = #{clientId, jdbcType=VARCHAR}


        and lh.client_id is null


        and lh.loginType = #{loginType, jdbcType=VARCHAR}


        and lh.login_time < #{loginEndTime, jdbcType=BIGINT}


        and lh.login_time > #{loginBeginTime, jdbcType=BIGINT}


        and u.user_account LIKE concat(#{userAccount,jdbcType=VARCHAR} , '%')


        and o.tax_code LIKE concat(#{taxCode,jdbcType=VARCHAR} , '%')


        and o.org_name LIKE concat(#{tenantName,jdbcType=VARCHAR} , '%')
