select * from MOIRAI_PRODUCT where product_id in (select distinct p.PRODUCT_ID from (select prd.PRODUCT_ID as PRODUCT_ID, prd.PRODUCT_NAME as PRODUCT_NAME, prd.BELONG_PRODUCT as BELONG_PRODUCT, prd.PRODUCT_TYPE as PRODUCT_TYPE from moirai_product prd left join moirai_product_resource res on prd.PRODUCT_ID = res.PRODUCT_ID WHERE res.RESOURCE_ID IN
#{item}
order by prd.ORDER_INDEX) p)

select

from moirai_product
where 1=1

    AND PRODUCT_ID = #{productId,jdbcType=BIGINT}


    AND PRODUCT_NAME LIKE CONCAT (CONCAT ('%', #{productName,jdbcType=VARCHAR}), '%')


    AND PRODUCT_TYPE = #{productType,jdbcType=VARCHAR}


    AND PRODUCT_CODE = #{productCode,jdbcType=VARCHAR}


    AND PRODUCT_OPENTYPE = #{productOpentype,jdbcType=VARCHAR}


    AND PRODUCT_DESCRIBE = #{productDescribe,jdbcType=VARCHAR}


    AND PRODUCT_MARK = #{productMark,jdbcType=CHAR}


    AND BELONG_PRODUCT = #{belongProduct,jdbcType=BIGINT}


    AND CREATETIME = #{createtime,jdbcType=BIGINT}


    AND CREATER = #{creater,jdbcType=VARCHAR}


    AND MODIFY_TIME = #{modifyTime,jdbcType=BIGINT}


    AND STARTTIME = #{starttime,jdbcType=BIGINT}


    AND ENDTIME = #{endtime,jdbcType=BIGINT}


    AND MODIFY_USER = #{modifyUser,jdbcType=VARCHAR}


    AND PRODUCT_URL = #{productUrl,jdbcType=VARCHAR}


    AND PRODUCT_ICON = #{productIcon,jdbcType=VARCHAR}


    AND ORDER_INDEX = #{orderIndex,jdbcType=BIGINT}

ORDER BY ORDER_INDEX

select pro.PRODUCT_ID, pro.PRODUCT_NAME, pro.PRODUCT_CODE, pro.PRODUCT_TYPE, pro.PRODUCT_DESCRIBE, pro.BELONG_PRODUCT, pro.PRODUCT_ICON, org.use_begintime as STARTTIME, org.use_endtime as ENDTIME from moirai_product pro left join moirai_org_product org on pro.PRODUCT_ID = org.PRODUCT_ID where org.org_id = #{orgId,jdbcType=BIGINT}
AND pro.PRODUCT_NAME LIKE CONCAT (CONCAT ('%', #{productName,jdbcType=VARCHAR}), '%')
order by pro.ORDER_INDEX
