mapper.select:
select

from sys_dict_detail
where 1=1

   AND DICT_DETAIL_ID = #{dictDetailId,jdbcType=BIGINT}


    AND DICT_CODE LIKE CONCAT(CONCAT('%',#{dictCode, jdbcType=VARCHAR}),'%')


    AND  DICT_NAME LIKE CONCAT(CONCAT('%',#{dictName, jdbcType=VARCHAR}),'%')


    AND ORDER_INDEX = #{orderIndex,jdbcType=INTEGER}


    AND ID = #{id,jdbcType=BIGINT}

order by ORDER_INDEX desc

mapper.delete:
delete from sys_dict_detail where 1=1

    AND DICT_DETAIL_ID in

        #{item}

mapper.insert:
insert into sys_dict_detail (
    DICT_DETAIL_ID,ID, DICT_CODE,DICT_NAME,ORDER_INDEX,USE_FLAG
  )
values (#{dictDetailId,jdbcType=BIGINT},#{id,jdbcType=BIGINT}, #{dictCode,jdbcType=VARCHAR},
   #{dictName,jdbcType=VARCHAR},#{orderIndex,jdbcType=INTEGER},#{useFlag,jdbcType=VARCHAR}
  )

insert into sys_dict_detail (
DICT_DETAIL_ID,ID, DICT_CODE,DICT_NAME,ORDER_INDEX,USE_FLAG
) VALUES

    (#{item.dictDetailId,jdbcType=BIGINT},#{item.id,jdbcType=BIGINT}, #{item.dictCode,jdbcType=VARCHAR},
    #{item.dictName,jdbcType=VARCHAR},#{item.orderIndex,jdbcType=INTEGER},#{item.useFlag,jdbcType=VARCHAR}
    )

mapper.select:
select d.DICT_DETAIL_ID,d.ID, d.DICT_CODE,d.DICT_NAME,d.ORDER_INDEX,d.USE_FLAG
 from sys_dict_detail d left join sys_dict s on d.ID = s.ID
where s.DICT_TYPE = #{dictType,jdbcType=VARCHAR};
